import React, { useState } from "react";
import { Button, Modal, Carousel } from "antd";

const ProjectModal = ({ open, onClose, count }) => {
  const imageRender = (count) => {
    const data = [];
    let i = null;
    for (i = 1; i < count; i++) {
      data.push(i);
    }
    data.map((i) => {
      return (
        <div>
          <img src={`/images/meta/meta-${i}.png`} alt="meta" />
        </div>
      );
    });
  };
  return (
    <Modal title="Modal 1000px width" centered open={open} onCancel={onClose}>
      <Carousel autoplay>
        {imageRender(count)}
        {/* {imageRender.forEach((i) => {
          return (
            <div>
              <img src={`/images/meta/meta-${i}.png`} alt="meta" />
            </div>
          );
        })} */}
      </Carousel>
    </Modal>
  );
};

export default ProjectModal;
