import {
  Routes,
  Route,
  useParam,
  useLocation,
  useNavigate,
} from "react-router-dom";
import { CSSTransition, TransitionGroup } from "react-transition-group";
import RouterList from "./router";

const findRouteConfig = (currentPath = "") => {
    let target = RouterList.find((route) => {
      if (route.path === "/") {
        return route.path === currentPath ?? window.location.pathname;
      }
      const regex = new RegExp(`^${route.path.replace(/:[^\/]*/g, ".*")}$`);
      // console.log(
      //   regex,
      //   currentPath ?? window.location.pathname,
      //   regex.test(currentPath ?? window.location.pathname)
      // );
      return regex.test(currentPath ?? window.location.pathname);
    });
    if (!target) return "default";

    return !!target ? target : {}; //background
  };

const RouterRender = ({ router, match }) => {
  let { location } = router;
  return (
    <TransitionGroup>
      <CSSTransition>
        <Routes>
          {RouterList.map((route, index) => {
            return (
              <Route
                key={index}
                path={route.path}
                element={<route.component {...route.props}></route.component>}
              ></Route>
            );
          })}
        </Routes>
      </CSSTransition>
    </TransitionGroup>
  );
};

export default RouterRender
